package javapackage;

public class PrimitiveDataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//integer dadatype
		
		byte mybyte = -127;
		System.out.println(mybyte);
		
		short myshort = 32045;
		System.out.println(myshort);
		
		int myinttt = 15487;
		System.out.println(myinttt);
		
		long mylong = 15555555555L;
		System.out.println(mylong);
		
		//Floating point
		
		float myfloating = 15.1234567f;
		System.out.println(myfloating);
		
		double mydouble = -56.5664d;
		System.out.println(mydouble);
		
		
		
		

	}

}
